<script setup>
import { onMounted } from 'vue';

const adjectifs = ["passionnés", "passionnés", "téméraires", "intéressés", "enthousiastes", "déterminés", "curieux", "mordus", "créatifs", "investis", "bolés", "aventuriers", "audacieux", "intrépides", "curieux", "amateurs", "connaisseurs", "experts", "casse-cou", "farfelus", "zinzins", "originaux", "rebels", "provocateurs", "Jedi", "Sith", "Hobbits", "Gremlins", "Avengers", "Titans", "Aliens", "Terminators", "Predators", "Vampires", "Zombies", "Transformers", "Minions",];
const couleurs = ["yellow", "yellow", "magenta", "cyan", "red", "green", "blue", "orange", "purple", "brown", "pink"];
onMounted(() => {
    adjectif();
    yeux();
    texteElse();
});
function texteElse() {
    document.getElementById("else").querySelectorAll("path").forEach(path => {
        let bbox = path.getBBox();
        path.dataset.width = bbox.width;
        path.dataset.height = bbox.height;
        path.style.transformOrigin = `${bbox.x + bbox.width / 2}px ${bbox.y + bbox.height / 2}px`;
    });
}
function adjectif() {
    var pos = Math.floor(random(0, 4) * adjectifs.length);
    document.getElementById("adjectif").innerHTML = adjectifs[pos];
}
function yeux() {
    var pos = Math.floor(random(0, 4) * couleurs.length);
    document.getElementById("yeux").setAttribute("fill", couleurs[pos]);
}
function random(decalage = 0, biais = 1) {
    return (Math.random() - decalage) ** biais;
}
function randomVal(val, decalage = 0, biais = 1) {
    return random(decalage, biais) * val;
}
const evt = {
    else: {
        mouseenter: e => {
            var path = e.explicitOriginalTarget;
            var prob = 0.8;
            var biais = 1;
            var transformations = [
                (Math.random() < prob) ? "" : `translateX(${randomVal(path.dataset.width, .5, biais)}px)`,
                (Math.random() < prob) ? "" : `translateY(${randomVal(path.dataset.height, .5, biais)}px)`,
                (Math.random() < prob) ? "" : `translateZ(${randomVal(path.dataset.height, .5, biais)}px)`,
                (Math.random() < prob) ? "" : `rotateX(${randomVal(180, .5, biais)}deg)`,
                (Math.random() < prob) ? "" : `rotateY(${randomVal(180, .5, biais)}deg)`,
                (Math.random() < prob) ? "" : `rotateZ(${randomVal(180, .5, biais)}deg)`,
                // (Math.random() < prob) ? "" : `scale(1, ${random(.5, biais) + 1})`,
                // (Math.random() < prob) ? "" : `scale(${random(.5, biais) + 1}, 1)`,
                // (Math.random() < prob) ? "" : `scale(${random(.5, biais) + 1})`,
                // (Math.random() < prob) ? "" : `skew(${randomVal(40, .5, biais)}deg, 0deg)`,
                // (Math.random() < prob) ? "" : `skew(0deg, ${randomVal(40, .5, biais)}deg)`,
            ];
            transformations = transformations.join(" ");
            path.style.transform = transformations;
            window.setTimeout(() => {
                path.style.transform = "";
            }, Math.random() ** 2 * 5000 + 1000);
        },
    }
};
</script>
